import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
// import { register } from './api';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleRegister = async (event) => {
    event.preventDefault();

    try {
      const response = await register(email, password);
      console.log(response); // Lidar com a resposta da API em caso de sucesso

      // Limpar os campos ap칩s o registro bem-sucedido
      setEmail('');
      setPassword('');

      // Exibir uma mensagem de sucesso (opcional)
      alert('Registro realizado com sucesso!');
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setError('Email j치 cadastrado. Tente outro.');
      } else {
        setError('Erro ao registrar. Tente novamente mais tarde.');
        console.error('Erro ao registrar:', error);
      }
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
      <div className="card p-4">
        <h2 className="text-center mb-4">Registrar</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleRegister}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              className="form-control"
              placeholder="Digite seu email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Senha</label>
            <input
              type="password"
              className="form-control"
              placeholder="Digite sua senha"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-primary btn-block">Registrar</button>
        </form>
      </div>
    </div>
  );
};

export default Register;
